import React, { Component } from "react";
import axios from "axios";

class Register extends Component {
	state = { 
		username: "",
		password: "",
        rpassword: ""};

	handleTest = () => {console.log("http://127.0.0.1:5000/register?username="+this.state.username+"&password="+this.state.password)}

	HandleRegister = (password,rpassword) =>{
		if(password == rpassword){
			Register()
		}
		else{
			console.log("Passwords don't match")
		}
		
	}

	Register = () => {
			axios
			.post(
				"http://127.0.0.1:5000/register?username="+this.state.username+"&password="+this.state.password)
			.then((res) => {
				console.log(res)
			}).catch(err => {
				window.alert("Something went wrong");
			})
			;
	};
	
	handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}

	render() {
		return (
			<main className="Register">
				<h1>Register</h1>
				<p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						required
						name="username"
						value={this.state.username}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Password"
						required
						name="password"
						value={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p className="Input">
					<input className="RPassword"
						type="Rpassword"
						placeholder="Password"
						required
						name="Rpassword"
						value={this.state.rpassword}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p>
					<button onClick={this.handleRegister}>Register</button>
				</p>
			</main>
		);
	}
}

export default Register;
