import React, { Component } from "react";
import axios from "axios";

class Login extends Component {
	state = { 
		username: "",
		password: "",};

	handleTest = () => {console.log("http://127.0.0.1:5000/login?username="+this.state.username+"&password="+this.state.password)}

	handleLogIn = () => {
			axios
			.get(
				"http://127.0.0.1:5000/login?username="+this.state.username+"&password="+this.state.password)
			.then((res) => {
				console.log(res)
			}).catch(err => {
				window.alert("Wrong Username Or Password");
			})
			;
	};
	
	handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}
	
	/*handleUsernameChange = (input) => {
		this.setState({ username: input.target.value });
	};

	handlePasswordChange = (input) => {
		this.setState({ password: input.target.value });
	};*/

	render() {
		return (
			<main className="LogIn">
				<h1>Log In</h1>
				<p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						required
						username={this.state.username}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Password"
						required
						password={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p>
					<button onClick={this.handleLogIn}>Log In</button>
				</p>
			</main>
		);
	}
}

export default Login;
