import React, { Component } from "react";
import axios from "axios";

class Login extends Component {
	state = { 
		username: "",
		password: "",
		formErrors: {usernamel: "", password: ""},
		usernameValid: false,
		passwordValid: false,
		formValid: false};

	handleTest = () => {console.log("http://127.0.0.1:5000/login?username="+this.state.username+"&password="+this.state.password)}

	handleLogIn = () => {
			axios
			.get(
				"http://127.0.0.1:5000/login?username="+this.state.username+"&password="+this.state.password)
			.then((res) => {
				console.log(res)
			}).catch(err => {
				window.alert("Wrong Username Or Password");
			})
			;
	};
	
	handleUserInput (e) {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value}, 
						() => { this.validateField(name, value) });
		}

	  validateField(fieldName, value) {
			let fieldValidationErrors = this.state.formErrors;
			let usernameValid = this.state.usernamelValid;
			let passwordValid = this.state.passwordValid;

			switch(fieldName) {
			case 'username':
				usernameValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);
				fieldValidationErrors.username = usernameValid ? '' : ' is invalid';
				break;
			case 'password':
				passwordValid = value.length >= 6;
				fieldValidationErrors.password = passwordValid ? '': ' is too short';
				break;
			default:
				break;
			}
			this.setState({formErrors: fieldValidationErrors,
							usernameValid: usernameValid,
							passwordValid: passwordValid
						}, this.validateForm);
		}

	validateForm() {
		this.setState({formValid: this.state.usernameValid && this.state.passwordValid});
	}

	errorClass(error) {
		return(error.length === 0 ? '' : 'has-error');
	}
	handleUsernameChange = (input) => {
		this.setState({ username: input.target.value });
	};

	handlePasswordChange = (input) => {
		this.setState({ password: input.target.value });
	};

	render() {
		return (
			<main className="LogIn">
				<h1>Log In</h1>
				<div className="panel panel-default">
          			<FormErrors formErrors={this.state.formErrors} />
        		</div>
				<div className={`form-group ${this.errorClass(this.state.formErrors.username)}`}>
				<label htmlFor="username">Username</label>
				<input type="username" required className="form-control" name="username"
					placeholder="Username"
					value={this.state.username}
					onChange={this.handleUserInput}  />
				</div>
				<p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						required
						username={this.state.username}
						onChange={this.handleUsernameChange}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Password"
						required
						password={this.state.password}
						onChange={this.handlePasswordChange}
					></input>
				</p>
				<div className='formErrors'>
					{Object.keys(formErrors).map((fieldName, i) => {
					if(formErrors[fieldName].length > 0){
						return (
						<p key={i}>{fieldName} {formErrors[fieldName]}</p>
						)        
					} else {
						return '';
					}
					})}
				</div>
				<p>
					<button onClick={this.handleLogIn}>Log In</button>
				</p>
			</main>
		);
	}
}

export default Login;
