import React, { Component } from "react";
import axios from "axios";

class Login extends Component {
	state = { 
		username: "",
		password: "",};

	handleTest= async () => {
		const json = JSON.stringify({ username:this.state.username, password:this.state.password });
		const res = await axios.post('http://127.0.0.1:5000/login', json, {headers: {'Content-Type': 'application/json'}});

		res.data.data; // '{"answer":42}'
		res.data.headers['Content-Type']; // 'application/json',
	}

	handleLogIn = () => {
		const headers = {'Content-Type': 'application/json'}
			axios
			.get(
				"http://127.0.0.1:5000/login",this.state,{headers:headers})
			.then((res) => {
				console.log(res)
			}).catch(err => {
				window.alert("Something went wrong");
			})
			;
	};
	
	handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}

	render() {
		return (
			<main className="LogIn">
				<h1>Log In</h1>
				<p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						required
						name="username"
						value={this.state.username}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Password"
						required
						name="password"
						value={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p>
					<button onClick={this.handleTest}>Log In</button>
				</p>
			</main>
		);
	}
}

export default Login;
