import React, { Component } from "react";
import axios from "axios";

class Register extends Component {
	state = { 
		username: "",
		email:"",
		password: "",
        rpassword: ""};

	handleRegister = () =>{
		if(this.state.password === this.state.rpassword){
			this.Register()
		}
		else{
			console.log("Passwords don't match")
		}
		
	}

	Register = async () => {
		const result = await axios.post('http://159.223.240.163/register', { username: this.state.username , password: this.state.password, email: this.state.email});
		console.log(result)
	};
	
	handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}

	render() {
		
		return (
			<main className="Register">
				<h1>Register</h1>
				<p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						required
						name="username"
						value={this.state.username}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input  className="Email"
						type="text"
						placeholder="Email"
						required
						name="email"
						value={this.state.email}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Password"
						required
						name="password"
						value={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p className="Input">
					<input className="Password"
						type="password"
						placeholder="Repeated Password"
						required
						name="rpassword"
						value={this.state.rpassword}
						onChange={this.handleUserInput}
					></input>
				</p>
				<p style={{float:"left"}}>Besides the email no other personal data will be collected</p>
				<input style={{marginTop:"1.7em"}} type="checkbox"></input>รง
				<p>
					<button onClick={this.handleRegister}>Register</button>
				</p>
		
			</main>
		);
		}
}

export default Register;
