import React, { Component } from "react";
import axios from "axios";

class Profile extends Component {
    state = { username: "",email:"",password:"",rpassword:""};

    handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}

    render() 
    {
        return(
            <main className="Profile">
                <h1>Profile</h1>
                <p className="Input">
					<input  className="Username"
						type="text"
						placeholder="Username"
						name="username"
						value={this.state.username}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p>
					<button onClick={this.handleLogIn}>Change Username</button>
				</p>
                <p className="Input">
					<input  className="Email"
						type="text"
						placeholder="Email"
						name="email"
						value={this.state.email}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p>
					<button onClick={this.handleLogIn}>Change Email</button>
				</p>
                <p className="Input">
					<input  className="Password"
						type="text"
						placeholder="Password"
						name="password"
						value={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p className="Input">
					<input  className="RPassword"
						type="text"
						placeholder="Repeat Password"
						name="rpassword"
						value={this.state.rpassword}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p>
					<button onClick={this.handleLogIn}>Change Password</button>
				</p>
                
            </main>
        );
    }
}

export default Profile;
