import React, { Component } from "react";
import axios from "axios";

class Profile extends Component {
    state = { username: "",email:"",password:"",rpassword:""};

    handleUserInput  = (e) => {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		}
    
    handleChangeInfo =  async () => {
        const result = await axios.post('http://127.0.0.1:5000/userchange', { username: this.state.username , password: this.state.password, email: this.state.email});
		console.log(result)
    }

    render() 
    {
        return(
            <main className="Profile">
                <h1>Profile</h1>
                <p className="Input">
					<input  className="Email"
						type="text"
						placeholder="Email"
						name="email"
						value={this.state.email}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p className="Input">
					<input  className="Password"
						type="text"
						placeholder="Password"
						name="password"
						value={this.state.password}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p className="Input">
					<input  className="RPassword"
						type="text"
						placeholder="Repeat Password"
						name="rpassword"
						value={this.state.rpassword}
						onChange={this.handleUserInput}
					></input>
				</p>
                <p>
					<button onClick={this.handleChangeInfo}>Change Info</button>
				</p>
                
            </main>
        );
    }
}

export default Profile;
